<template>
 <div>
     <transition name="slide-fade" mode="out-in">
         <router-view class="child-view"></router-view>
     </transition>
     <div class="footer">
         <div class="mainNavigation" style="width: 750px;" >
             <div class="footerNavigation" @click="jumpHome()">
                 <image :src="navigationIcon_1" style="width: 40px;height: 40px"></image>
                 <div>
                     <text :class="[navigationFont_color_1]"  >首页</text>
                 </div>
             </div>
             <div class="footerNavigation" @click="jumpProject()">
                 <image :src="navigationIcon_2" style="width: 40px;height: 40px"></image>
                 <text :class="[navigationFont_color_2]" >项目</text>
             </div>
             <div class="footerNavigation" @click="jumpHire()">
                 <image :src="navigationIcon_3" style="width: 40px;height: 40px"></image>
                 <text :class="[navigationFont_color_3]" >雇佣</text>
             </div>
             <div class="footerNavigation" @click="jumpMe">
                 <image :src="navigationIcon_4" style="width: 40px;height: 40px"></image>
                 <text :class="[navigationFont_color_4]" >我的</text>
             </div>
         </div>
     </div>

 </div>

</template>
<script>
    var modal = weex.requireModule('modal');
    var stream = weex.requireModule('stream')
    var navigator = weex.requireModule('navigator')

    import router from '../router'
    import util from '../util'
    export default {
        data () {
            return {
                form: {
                    username: '',
                    password: ''
                },
               path:"",
                navigationIcon_1:"",
                navigationFont_color_1:'',
                navigationIcon_2:"",
                navigationFont_color_2:'',
                navigationIcon_3:"",
                navigationFont_color_3:'',
                navigationIcon_4:"",
                navigationFont_color_4:'',
            }
        },
        methods: {
            jumpMe () {
                router.push('/my_info')
            },
            jumpHome() {
                router.push('/')
            },
            jumpProject() {
                router.push('/project_list')
            },
            jumpHire() {
                router.push('/hire')
            },
            //监察路径
            fetchData(){
                this.path = this.$route.path;
                if(this.path=='/' ){
                    this.navigationIcon_1='data:image/png;base64,';
                    this.navigationFont_color_1='footerFontSize'
                }else {
                    this.navigationIcon_1="data:image/png;base64,";
                    this.navigationFont_color_1='footerFontSize_2';
                }

                if(this.path=='/project_list'){
                    this.navigationIcon_2='data:image/png;base64,';
                    this.navigationFont_color_2='footerFontSize'
                }else {
                    this.navigationIcon_2="data:image/png;base64,";
                    this.navigationFont_color_2='footerFontSize_2';
                }

                if(this.path=='/hire'){
                    this.navigationIcon_3='data:image/png;base64,';
                    this.navigationFont_color_3='footerFontSize'
                }else {
                    this.navigationIcon_3="data:image/png;base64,";
                    this.navigationFont_color_3='footerFontSize_2';
                }

                if(this.path=='/my_info' || this.path=='/login'){
                    this.navigationIcon_4='data:image/png;base64,';
                    this.navigationFont_color_4='footerFontSize'
                }else {
                    this.navigationIcon_4="data:image/png;base64,";
                    this.navigationFont_color_4='footerFontSize_2';
                }
            }
        },
        watch: {
            '$route': 'fetchData'
        },
        created(){
            this.fetchData();
            util.initIconFont ()
        }
    }
</script>
